+    for (size_t i = 0; i < pos; ++i) {
+        hrp += LowerCase(str[i]);
+    }
+    if (!VerifyChecksum(hrp, values)) {
+        return std::make_pair(std::string(), data());
+    }
+    return std::make_pair(hrp, data(values.begin(), values.end() - 6));

