Thanks @promag - I've addressed your comments.

86d3394 -> 74a9e81 . Only change is addressing promag comments:

```
diff --git a/src/wallet/rpcwallet.cpp b/src/wallet/rpcwallet.cpp
index aeea7af..50495d0 100644
--- a/src/wallet/rpcwallet.cpp
+++ b/src/wallet/rpcwallet.cpp
@@ -3739,9 +3739,9 @@ static UniValue AddressBookDataToJSON(const CAddressBookData& data, const bool v
 {
     UniValue ret(UniValue::VOBJ);
     if (verbose) {
-        ret.push_back(Pair("name", data.name));
+        ret.pushKV("name", data.name);
     }
-    ret.push_back(Pair("purpose", data.purpose));
+    ret.pushKV("purpose", data.purpose);
     return ret;
 }
 
@@ -3889,7 +3889,7 @@ UniValue getaddressesbylabel(const JSONRPCRequest& request)
     UniValue ret(UniValue::VOBJ);
     for (const std::pair<CTxDestination, CAddressBookData>& item : pwallet->mapAddressBook) {
         if (item.second.name == label) {
-            ret.push_back(Pair(EncodeDestination(item.first), AddressBookDataToJSON(item.second, false)));
+            ret.pushKV(EncodeDestination(item.first), AddressBookDataToJSON(item.second, false));
         }
     }
 
