+uint64_t Combination(uint64_t n, uint64_t k) {
+    if (n - k < k) k = n - k;
+    uint64_t ret = 1;
+    for (uint64_t i = 1; i <= k; ++i) {
+        ret = (ret * n) / i;
+        --n;
+    }
+    return ret;
+}
