+    thread_list.reserve(threads);
+    outputs.resize(threads);
+    for (uint32_t i = 0; i < threads; ++i) {
+        thread_list.emplace_back([=,&outputs](){ TestAll(bits, impl, count, i, threads, outputs[i]); });
