+                                              ? MempoolAcceptResult(*ws.m_ptx, ws.m_state, /* finished */ true)
+                                              : MempoolAcceptResult(*ws.m_ptx, ws.m_state, /* finished */ false);
+            };
+            std::transform(workspaces.begin(), workspaces.end(), std::back_inserter(results), failed_or_unfinished);
