+++ b/src/test/versionbits_tests.cpp
@@ -275,6 +275,31 @@ BOOST_AUTO_TEST_CASE(versionbits_sanity)
     }
 }
 
+static bool check_bit_set(const CBlockIndex* lastBlock, const Consensus::Params& params, int64_t bit)
+{
+    if ((ComputeBlockVersion(lastBlock, params) & (1 << bit)) == 0) {
+        BOOST_ERROR("version bit not set!");
+        return false;
+    }
+    if ((ComputeBlockVersion(lastBlock, params) & VERSIONBITS_TOP_MASK) != VERSIONBITS_TOP_BITS) {
+        BOOST_ERROR("versionbits top mask not set!");
+        return false;
+    }
+    return true;
+}
+
+static bool check_bit_unset(const CBlockIndex* lastBlock, const Consensus::Params& params, int64_t bit)
+{
+    if ((ComputeBlockVersion(lastBlock, params) & (1 << bit)) != 0) {
+        BOOST_ERROR("version bit set!");
+        return false;
+    }
+    if ((ComputeBlockVersion(lastBlock, params) & VERSIONBITS_TOP_MASK) != VERSIONBITS_TOP_BITS) {
+        BOOST_ERROR("versionbits top mask not set!");
+        return false;
+    }
+    return true;
+}
 static void check_computeblockversion(const Consensus::Params& params, Consensus::DeploymentPos dep)
 {
     // This implicitly uses versionbitscache, so clear it every time
@@ -318,9 +343,10 @@ static void check_computeblockversion(const Consensus::Params& params, Consensus
         // earlier time, so will transition from DEFINED to STARTED at the
         // end of the first period by mining blocks at nTime == 0
         lastBlock = firstChain.Mine(params.nMinerConfirmationWindow - 1, nTime, VERSIONBITS_LAST_OLD_BLOCK_VERSION).Tip();
-        BOOST_CHECK_EQUAL(ComputeBlockVersion(lastBlock, params) & (1<<bit), 0);
+        BOOST_CHECK(check_bit_unset(lastBlock, params, bit));
+
         lastBlock = firstChain.Mine(params.nMinerConfirmationWindow, nTime, VERSIONBITS_LAST_OLD_BLOCK_VERSION).Tip();
-        BOOST_CHECK((ComputeBlockVersion(lastBlock, params) & (1<<bit)) != 0);
+        BOOST_CHECK(check_bit_set(lastBlock, params, bit));
         // then we'll keep mining at nStartTime...
     } else {
         // use a time 1s earlier than start time to check we stay DEFINED
@@ -328,30 +354,28 @@ static void check_computeblockversion(const Consensus::Params& params, Consensus
 
