     Blocks(uint32_t start_time, int32_t signal, int32_t no_signal) : m_start_time{start_time}, m_signal{signal}, m_no_signal{no_signal} { }
 
-    ~Blocks()
-    {
-        for (auto& v : blocks) {
-            delete v;
-        }
-        blocks.clear();
-    }
-
     size_t size() const { return blocks.size(); }
 
     CBlockIndex* tip() const
     {
-        return blocks.empty() ? nullptr : blocks.back();
+        return blocks.empty() ? nullptr : blocks.back().get();
     }
 
     CBlockIndex* mine_block(bool signal)
