+{
+    uint8_t remote_set_size = local_set_size + actual_local_missing - actual_remote_missing;
+    uint8_t set_size_diff = std::abs(local_set_size - remote_set_size);
+    uint8_t min_size = std::min(local_set_size, remote_set_size);
