+/** Make a polynomial monic. */
+template<typename F>
+typename F::Elem MakeMonic(std::vector<typename F::Elem>& a, const F& field) {
+    CHECK_SAFE(a.back() != 0);
+    if (a.back() == 1) return 0;
+    auto inv = field.Inv(a.back());
+    typename F::Multiplier mul(field, inv);
+    a.back() = 1;
+    for (size_t i = 0; i < a.size() - 1; ++i) {
+        a[i] = mul(a[i]);
+    }
+    return inv;
