+/** Compute the GCD of two polynomials, putting the result in a. b will be cleared. */
+template<typename F>
+void GCD(std::vector<typename F::Elem>& a, std::vector<typename F::Elem>& b, const F& field) {
+    if (a.size() < b.size()) std::swap(a, b);
+    while (b.size() > 0) {
+        if (b.size() == 1) {
+            a.resize(1);
+            a[0] = 1;
+            return;
+        }
+        MakeMonic(b, field);
+        PolyMod(b, a, field);
+        std::swap(a, b);
+    }
