+/** Square a polynomial. */
+template<typename F>
+void Sqr(std::vector<typename F::Elem>& poly, const F& field) {
+    if (poly.size() == 0) return;
+    poly.resize(poly.size() * 2 - 1);
+    for (int x = poly.size() - 1; x >= 0; --x) {
+        poly[x] = (x & 1) ? 0 : field.Sqr(poly[x / 2]);
+    }
