+template<typename F>
+void AddToOddSyndromes(std::vector<typename F::Elem>& osyndromes, typename F::Elem data, const F& field) {
+    auto sqr = field.Sqr(data);
+    typename F::Multiplier mul(field, sqr);
+    for (auto& osyndrome : osyndromes) {
+        osyndrome ^= data;
+        data = mul(data);
+    }
+}
